import React, { useState, useEffect } from 'react';

const API_URL = 'http://api.bestimate.co.kr';  // API URL 설정

const MatchingManagement = ({ companyType }) => {
  const [estimates, setEstimates] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // 로그인한 사용자만 접근할 수 있도록 JWT 토큰 확인
    const fetchEstimates = async () => {
      const token = localStorage.getItem('token'); // 토큰을 localStorage에서 가져옴
      console.log('Token:', token);  // 디버깅용 로그 추가
      
      if (!token) {
        setError('로그인이 필요합니다.');
        setLoading(false);
        return;
      }

      try {
        const response = await fetch(`${API_URL}/api/estimates/user`, {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${token}`, // JWT 토큰을 헤더에 추가
          },
        });

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const data = await response.json();
        setEstimates(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    if (companyType === '공장') {
      fetchEstimates();
    } else {
      setLoading(false);
    }
  }, [companyType]);

  if (loading) {
    return <p>로딩 중...</p>;
  }

  if (error) {
    return <p>에러 발생: {error}</p>;
  }

  return (
    <div>
      <h1>매칭 관리</h1>
      {companyType === '공장' ? (
        estimates.length === 0 ? (
          <p>현재 보낸 견적이 없습니다.</p>
        ) : (
          <div>
            {estimates.map((estimate) => (
              <div key={estimate.id} className="estimate-item">
                <h3>매칭된 의뢰</h3>
                <p><strong>제목:</strong> {estimate.post?.title || 'N/A'}</p>
                <p><strong>내용:</strong> {estimate.post?.content || 'N/A'}</p>
                <p><strong>보낸 견적 수량:</strong> {estimate.quantities}</p>
                <p><strong>보낸 견적 가격:</strong> {estimate.prices}</p>
                <p><strong>작성자 이메일:</strong> {estimate.user?.email || 'N/A'}</p>
              </div>
            ))}
          </div>
        )
      ) : (
        <p>해당사항 없음</p>
      )}
    </div>
  );
};

export default MatchingManagement;
